import { getEntries, Hackathon } from "@/lib/db";
import { formatDate } from "@/lib/utils";
import Link from "next/link";
import { MapPin, Calendar, ExternalLink } from "lucide-react";

export const metadata = {
    title: "Hackathons | Academic Portfolio",
    description: "Records of competitive programming and prototyping events.",
};

export default async function HackathonsPage() {
    const hackathons = await getEntries<Hackathon>("hackathons.json");
    const sorted = hackathons.sort((a, b) =>
        new Date(b.timestamp).getTime() - new Date(a.timestamp).getTime()
    );

    return (
        <div className="space-y-8">
            <div className="border-b pb-4">
                <h1 className="text-3xl font-serif font-bold text-zinc-900">Hackathons</h1>
                <p className="text-zinc-500 mt-2">Intensive prototyping and engineering competitions.</p>
            </div>

            <div className="grid gap-6">
                {sorted.map((item) => (
                    <article key={item.id} className="border border-zinc-200 rounded-lg p-6 hover:shadow-sm transition-shadow">
                        <div className="flex flex-col md:flex-row md:items-start justify-between gap-2 mb-4">
                            <div>
                                <h2 className="text-xl font-medium text-zinc-900">{item.title}</h2>
                                <div className="flex flex-wrap gap-4 mt-2 text-sm text-zinc-500">
                                    <span className="flex items-center gap-1"><MapPin className="w-3.5 h-3.5" /> {item.location}</span>
                                    <span className="flex items-center gap-1"><Calendar className="w-3.5 h-3.5" /> {item.dates}</span>
                                </div>
                            </div>
                            {item.projectLink && (
                                <Link href={item.projectLink} target="_blank" className="shrink-0 inline-flex items-center gap-1 text-sm font-medium text-zinc-900 hover:text-zinc-700">
                                    View Submission <ExternalLink className="w-3.5 h-3.5" />
                                </Link>
                            )}
                        </div>
                        <p className="text-zinc-600 mb-4">{item.content}</p>
                    </article>
                ))}
                {sorted.length === 0 && (
                    <p className="text-zinc-500 italic">No hackathons recorded.</p>
                )}
            </div>
        </div>
    );
}

import { getEntries, BaseEntry } from "@/lib/db";
import { formatDate } from "@/lib/utils";

export const metadata = {
    title: "Timeline | Academic Portfolio",
    description: "Chronological view of all activities.",
};

export default async function TimelinePage() {
    // Aggregate all types of entries for a unified timeline
    const projects = await getEntries<BaseEntry>("projects.json");
    const hackathons = await getEntries<BaseEntry>("hackathons.json");
    const timelineEvents = await getEntries<BaseEntry>("timeline.json");

    const allEvents = [...projects, ...hackathons, ...timelineEvents].sort((a, b) =>
        new Date(b.timestamp).getTime() - new Date(a.timestamp).getTime()
    );

    return (
        <div className="space-y-8">
            <div className="border-b pb-4">
                <h1 className="text-3xl font-serif font-bold text-zinc-900">Timeline</h1>
                <p className="text-zinc-500 mt-2">A unified chronological view of all activities.</p>
            </div>

            <div className="relative border-l border-zinc-200 ml-3 space-y-8 pb-8">
                {allEvents.map((event) => (
                    <div key={`${event.id}-${event.title}`} className="relative pl-8">
                        <span className="absolute -left-[5px] top-2 h-2.5 w-2.5 rounded-full bg-zinc-300 ring-4 ring-white" />
                        <div className="flex flex-col sm:flex-row sm:items-baseline sm:justify-between gap-1">
                            <h3 className="text-lg font-medium text-zinc-900">{event.title}</h3>
                            <span className="text-sm font-mono text-zinc-400">{formatDate(event.timestamp)}</span>
                        </div>
                        {/* @ts-ignore */}
                        {event.category && (
                            // @ts-ignore
                            <span className="inline-block mt-1 px-2 py-0.5 text-xs font-medium bg-zinc-100 text-zinc-600 rounded capitalize">
                                {/* @ts-ignore */}
                                {event.category}
                            </span>
                        )}
                    </div>
                ))}
                {allEvents.length === 0 && (
                    <p className="pl-8 text-zinc-500 italic">No events found.</p>
                )}
            </div>
        </div>
    );
}

'use server'

import { addEntry, BaseEntry, Project, Hackathon } from "@/lib/db"
import { auth } from "@/lib/auth"
import { revalidatePath } from "next/cache"
import { z } from "zod"
import { redirect } from "next/navigation"

// Validation Schemas
const projectSchema = z.object({
    title: z.string().min(1),
    description: z.string().min(1),
    content: z.string().min(1),
    technologies: z.string().transform(str => str.split(',').map(s => s.trim()).filter(Boolean)),
    repoUrl: z.string().optional(),
    demoUrl: z.string().optional(),
})

const hackathonSchema = z.object({
    title: z.string().min(1),
    location: z.string().min(1),
    dates: z.string().min(1),
    content: z.string().min(1),
    projectLink: z.string().optional(),
}) // Simplified for brevity

export type ActionState = {
    message?: string
    error?: string
}

export async function createEntry(prevState: ActionState, formData: FormData) {
    const session = await auth()
    if (!session?.user) {
        return { error: "Unauthorized" }
    }

    const type = formData.get("type") as string
    const username = session.user.username || session.user.email || "unknown"

    try {
        if (type === "project") {
            const rawData = {
                title: formData.get("title"),
                description: formData.get("description"),
                content: formData.get("content"),
                technologies: formData.get("technologies"),
                repoUrl: formData.get("repoUrl"),
                demoUrl: formData.get("demoUrl"),
            }

            const validated = projectSchema.parse(rawData)

            const entry: Project = {
                id: crypto.randomUUID(),
                title: validated.title,
                category: 'project',
                description: validated.description,
                content: validated.content,
                technologies: validated.technologies,
                links: [],
                timestamp: new Date().toISOString(),
                author: username,
            }

            if (validated.repoUrl) entry.links.push({ label: "GitHub", url: validated.repoUrl })
            if (validated.demoUrl) entry.links.push({ label: "Demo", url: validated.demoUrl })

            await addEntry("projects.json", entry)
            revalidatePath("/projects")
            revalidatePath("/timeline")
        } else if (type === "hackathon") {
            const rawData = {
                title: formData.get("title"),
                location: formData.get("location"),
                dates: formData.get("dates"),
                content: formData.get("content"),
                projectLink: formData.get("projectLink"),
            }
            const validated = hackathonSchema.parse(rawData)

            const entry: Hackathon = {
                id: crypto.randomUUID(),
                title: validated.title,
                category: 'hackathon',
                timestamp: new Date().toISOString(),
                author: username,
                location: validated.location,
                dates: validated.dates,
                content: validated.content,
                projectLink: validated.projectLink || undefined
            }
            await addEntry("hackathons.json", entry)
            revalidatePath("/hackathons")
            revalidatePath("/timeline")
        } else if (type === "reflection") {
            // Implementation for simple reflection (timeline only)
            const entry: BaseEntry = {
                id: crypto.randomUUID(),
                title: formData.get("title") as string,
                timestamp: new Date().toISOString(),
                // @ts-ignore
                category: 'reflection',
                author: username
            }
            await addEntry("timeline.json", entry)
            revalidatePath("/timeline")
        } else {
            return { error: "Invalid type" }
        }

        return { message: "Entry created successfully" }

    } catch (e) {
        console.error(e)
        return { error: "Failed to create entry" }
    }
}

import NextAuth from "next-auth"
import GitHub from "next-auth/providers/github"

export const { handlers, auth, signIn, signOut } = NextAuth({
    providers: [GitHub],
    callbacks: {
        async signIn({ user, profile }) {
            if (!process.env.ADMIN_GITHUB_USERNAME) {
                console.warn("ADMIN_GITHUB_USERNAME not set")
                return false
            }

            // Strict check: Only the admin can sign in
            // @ts-ignore - profile.login is available on GitHub profile
            const username = profile?.login as string

            if (!username || username.toLowerCase() !== process.env.ADMIN_GITHUB_USERNAME.toLowerCase()) {
                return false // Deny access
            }

            return true
        },
        async jwt({ token, profile }) {
            if (profile) {
                // @ts-ignore
                token.username = profile.login
            }
            return token
        },
        async session({ session, token }) {
            if (session.user && token.username) {
                session.user.username = token.username as string
            }
            return session
        },
        authorized({ auth, request: { nextUrl } }) {
            const isLoggedIn = !!auth?.user
            const isOnAdmin = nextUrl.pathname.startsWith('/admin')

            if (isOnAdmin) {
                if (isLoggedIn) return true
                return false // Redirect to login
            }
            return true
        },
    },
})

import fs from 'fs/promises'
import path from 'path'

const DATA_DIR = path.join(process.cwd(), 'data')

export interface BaseEntry {
    id: string
    title: string
    timestamp: string // ISO date
    author: string
}

export interface Project extends BaseEntry {
    category: 'project'
    description: string
    technologies: string[]
    links: { label: string; url: string }[]
    content: string // Markdown or HTML
}

export interface Hackathon extends BaseEntry {
    category: 'hackathon'
    location: string
    dates: string
    projectLink?: string
    content: string
}

// Ensure data directory exists
const ensureDataDir = async () => {
    try {
        await fs.access(DATA_DIR)
    } catch {
        await fs.mkdir(DATA_DIR, { recursive: true })
    }
}

export async function getEntries<T extends BaseEntry>(filename: string): Promise<T[]> {
    await ensureDataDir()
    const filePath = path.join(DATA_DIR, filename)
    try {
        const data = await fs.readFile(filePath, 'utf-8')
        return JSON.parse(data) as T[]
    } catch (error) {
        return []
    }
}

export async function addEntry<T extends BaseEntry>(filename: string, entry: T): Promise<void> {
    await ensureDataDir()
    const filePath = path.join(DATA_DIR, filename)
    const entries = await getEntries<T>(filename)
    entries.push(entry)
    await fs.writeFile(filePath, JSON.stringify(entries, null, 2))
}

